import './booking.css';
import Search from '/components/Search';

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
          list: [
          ]
        }
        this.handleChange = this.handleChange.bind(this);
    }


    handleChange(e) {


        if (e.target.value.length >=2) {
            alert(e.target.value);
        }





