    render() {
      return(
        <div className="content">
            <div className="container">
                <section className="section">
                    <div id="searchbox">
                        <form autoComplete="off" class="c-searchbox">
                            <h2 class="c-searchbox-title">Where are you going?</h2>
                            <div>
                                <label for="location" class="c-searchbox-label">Pick-up Location</label>
                                <div>
                                    <Search />



                                    <input type="text" id="location" className="input c-search-input" onChange={this.handleChange} placeholder="city, airport, station, region and district..." />
                                    <span id="resultsContainer"></span>
                                </div>
                            </div>
                        </form>
                    </div>
                </section>
            </div>
        </div>
      )
    }
  }

  ReactDOM.render(<App />, document.getElementById('app'));
